// Arquivo: frontend/src/pages/LoginPage.js
import React, { useState } from 'react';

const LoginPage = () => {
  // 'useState' é um "Hook" do React para guardar o estado do componente.
  // Vamos usá-lo para armazenar o que o usuário digita nos campos.
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (event) => {
    // Previne o comportamento padrão do formulário, que é recarregar a página.
    event.preventDefault(); 

    // Por enquanto, vamos apenas mostrar os dados no console.
    // No próximo passo, faremos a chamada para a API aqui.
    console.log('Tentativa de login com:', { email, password }); 
  };

  return (
    <div>
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="email">Email:</label>
          <input 
            type="email" 
            id="email" 
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="password">Senha:</label>
          <input 
            type="password" 
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Entrar</button>
      </form>
    </div>
  );
};

export default LoginPage;
